package Q_9;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int j=5;
		switch(j) {
		case 0:
			System.out.println("J="+1);
		    break;
		case 1:
			System.out.println("J="+2);
		    break;
		case 5:
			System.out.println("J="+5);
		    break;
		default:
			System.out.println("No value");
		    break;
		 
		}

	}

}
