package New;

public class Q_22 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String theString = "This IS a mix of UPPERcase and lowerCASE";

		String uppercase = theString.toUpperCase();
		String lowercase = theString.toLowerCase();
		System.out.println("Uppercase string="+uppercase);
		System.out.println("Lowercase string="+lowercase);

	}

}
/*Output
Uppercase string=THIS IS A MIX OF UPPERCASE AND LOWERCASE
Lowercase string=this is a mix of uppercase and lowercase
*/
